insert into cytaty (autor, cytat) values ('~Albert Einstein',        '„Są dwie drogi, aby przeżyć życie. Jedna to żyć tak, jakby nic nie było cudem. Druga to żyć tak, jakby cudem było wszystko.”');
insert into cytaty (autor, cytat) values ('~Simon Beckett',          '„Nie ma drugich szans, są tylko inne. Jakąkolwiek podejmiesz decyzję, życie zawsze będzie inne niż wtedy, gdybyś podjął inną.”');
insert into cytaty (autor, cytat) values ('~Dante Alighieri ',       '„Trzy rzeczy zostały z raju: gwiazdy, kwiaty i oczy dziecka.”');
insert into cytaty (autor, cytat) values ('~Steve Jobs',             '„Twój czas jest ograniczony, więc nie marnuj go na bycie kimś, kim nie jesteś.”');
insert into cytaty (autor, cytat) values ('~Stanisław Lem',          '„Bądź dobrej myśli, bo po co być złej.”');
insert into cytaty (autor, cytat) values ('~Oprah Winfrey',          '„Bądź wdzięczny za to, co masz, a będziesz miał tego więcej. Jeśli koncentrujesz się na tym, czego nie masz, nigdy nie będziesz miał wystarczająco dużo.”');
insert into cytaty (autor, cytat) values ('~Dalajlama',              '„Raz do roku pojedź w miejsce, w którym jeszcze nie byłeś.”');
insert into cytaty (autor, cytat) values ('~św. Augustyn',           '„Świat jest książką i ci, którzy nie podróżują, czytają tylko jedną stronę.”');
insert into cytaty (autor, cytat) values ('~Nicolas Bouvier',        '„Dalekie podróże mają to do siebie, że przywozi się z nich coś zupełnie innego niż to, po co się pojechało.”');
insert into cytaty (autor, cytat) values ('~Ernest Hemingway',       '„Kochać to chcieć przemierzyć cały świat we dwoje, po to, by nie było miejsca na ziemi wolnego od wspólnych wspomnień.”');
insert into cytaty (autor, cytat) values ('~Ryszard Kapuściński',    '„Wszak istnieje coś takiego jak zarażenie podróżą i jest to rodzaj choroby w gruncie rzeczy nieuleczalnej.”');
insert into cytaty (autor, cytat) values ('~Susan Sontag',           '„Nie byłam wszędzie, ale wszędzie na pewno jest na mojej liście.”');
insert into cytaty (autor, cytat) values ('~Albert Einstein',        '„Najcenniejszych rzeczy w życiu nie nabywa się za pieniądze.”');
insert into cytaty (autor, cytat) values ('~Walt Disney',            '„Sposobem na zaczęcie jest skończenie mówienia i podjęcie działania.”');
insert into cytaty (autor, cytat) values ('~Albert Schweitzer',      '„Szczęście to jedyna rzecz, która się mnoży, gdy się ją dzieli.”');
